;; gorilla-repl.fileformat = 1

;; **
;;; # Move a shape to the center of the scene
;;; 
;;; Say you have a shape inside a picture, and you want to move it somewhere else inside the same picture.
;;; 
;; **

;; @@
(ns unwavering-briars
 (:require
  [opencv4.utils :as u]
  [opencv4.colors.html :refer :all]
  [opencv4.core :refer :all]))
;; @@
;; =>
;;; {"type":"html","content":"<span class='clj-nil'>nil</span>","value":"nil"}
;; <=

;; **
;;; The plan is defined as follow:
;;; 
;;; - load the picture, and add a quick border to get it to show properly here.
;;; - go into HSV color space to be able to select only the object we want use a color range
;;; - create a mask 
;; **

;; @@
(def img 
  (-> "resources/morph/cjy6M.jpg" 
      (imread IMREAD_REDUCED_COLOR_2)
      (copy-make-border! 1 1 1 1 
		BORDER_CONSTANT 
        (->scalar "#aabbcc"))))

(u/mat-view img)
;; @@
;; =>
;;; {"type":"html","content":"<img src=\"data:image/png;base64,\" width=\"321\" height=\"241\" alt=\"\" />","value":"#object[java.awt.image.BufferedImage 0x1d0bb7dc \"BufferedImage@1d0bb7dc: type = 5 ColorModel: #pixelBits = 24 numComponents = 3 color space = java.awt.color.ICC_ColorSpace@1a40145 transparency = 1 has alpha = false isAlphaPre = false ByteInterleavedRaster: width = 321 height = 241 #numDataElements 3 dataOff[0] = 2\"]"}
;; <=

;; **
;;; ## thresh hold on yellow in hsv space
;; **

;; @@
(def mask-on-yellow
  (-> 
    img 
    clone 
    (cvt-color! COLOR_BGR2HSV) 
    (in-range! (new-scalar 20 100 100) (new-scalar 30 255 255))))

(u/mat-view mask-on-yellow)
;; @@
;; =>
;;; {"type":"html","content":"<img src=\"data:image/png;base64,\" width=\"321\" height=\"241\" alt=\"\" />","value":"#object[java.awt.image.BufferedImage 0x5238ec87 \"BufferedImage@5238ec87: type = 10 ColorModel: #pixelBits = 8 numComponents = 1 color space = java.awt.color.ICC_ColorSpace@55fc79e5 transparency = 1 has alpha = false isAlphaPre = false ByteInterleavedRaster: width = 321 height = 241 #numDataElements 1 dataOff[0] = 0\"]"}
;; <=

;; **
;;; ## find contours, and select first 1, (actually only one)
;; **

;; @@
(def contours (new-arraylist))
(find-contours mask-on-yellow contours (new-mat) RETR_EXTERNAL CHAIN_APPROX_SIMPLE)
(count contours)
;; @@
;; =>
;;; {"type":"html","content":"<span class='clj-unkown'>1</span>","value":"1"}
;; <=

;; @@
(def background-color  (->scalar "#000000"))
; mask type CV_8UC1 is important !!
(def mask (new-mat (rows img) (cols img) CV_8UC1 background-color))

(u/mat-view mask)
;; @@
;; =>
;;; {"type":"html","content":"<img src=\"data:image/png;base64,\" width=\"321\" height=\"241\" alt=\"\" />","value":"#object[java.awt.image.BufferedImage 0x28eeb601 \"BufferedImage@28eeb601: type = 10 ColorModel: #pixelBits = 8 numComponents = 1 color space = java.awt.color.ICC_ColorSpace@55fc79e5 transparency = 1 has alpha = false isAlphaPre = false ByteInterleavedRaster: width = 321 height = 241 #numDataElements 1 dataOff[0] = 0\"]"}
;; <=

;; **
;;; Here we draw the one contour we have found, using the *FILLED* setting of draw-contours. 
;; **

;; @@
(draw-contours mask contours 0 (new-scalar 255.0) FILLED)
(u/mat-view mask)
;; @@
;; =>
;;; {"type":"html","content":"<img src=\"data:image/png;base64,\" width=\"321\" height=\"241\" alt=\"\" />","value":"#object[java.awt.image.BufferedImage 0x7d60d229 \"BufferedImage@7d60d229: type = 10 ColorModel: #pixelBits = 8 numComponents = 1 color space = java.awt.color.ICC_ColorSpace@55fc79e5 transparency = 1 has alpha = false isAlphaPre = false ByteInterleavedRaster: width = 321 height = 241 #numDataElements 1 dataOff[0] = 0\"]"}
;; <=

;; @@
(def box 
  (bounding-rect (first contours)))
(def item 
  (submat img box))

(u/mat-view item)

(def segmented-item
  (new-mat (rows item) (cols item) CV_8UC3 background-color))
(copy-to item segmented-item (submat mask box) )

(u/mat-view segmented-item)
;; @@
;; =>
;;; {"type":"html","content":"<img src=\"data:image/png;base64,\" width=\"52\" height=\"48\" alt=\"\" />","value":"#object[java.awt.image.BufferedImage 0x431e70fb \"BufferedImage@431e70fb: type = 5 ColorModel: #pixelBits = 24 numComponents = 3 color space = java.awt.color.ICC_ColorSpace@1a40145 transparency = 1 has alpha = false isAlphaPre = false ByteInterleavedRaster: width = 52 height = 48 #numDataElements 3 dataOff[0] = 2\"]"}
;; <=

;; @@

(def center
  (new-point (/ (.cols img ) 2 ) (/ (.rows img) 2)))

(def center-box
  (new-rect
    (- (.-x center ) (/ (.-width box) 2))
    (- (.-y center ) (/ (.-height box) 2))
    (.-width box)
    (.-height box)))
;; @@
;; =>
;;; {"type":"html","content":"<span class='clj-var'>#&#x27;unwavering-briars/center-box</span>","value":"#'unwavering-briars/center-box"}
;; <=

;; @@

  (def result (new-mat (rows img) (cols img) CV_8UC3 background-color))

  (def final (submat result center-box))
  (copy-to segmented-item final (new-mat))

(u/mat-view result)
;; @@
;; =>
;;; {"type":"html","content":"<img src=\"data:image/png;base64,\" width=\"321\" height=\"241\" alt=\"\" />","value":"#object[java.awt.image.BufferedImage 0x373cba75 \"BufferedImage@373cba75: type = 5 ColorModel: #pixelBits = 24 numComponents = 3 color space = java.awt.color.ICC_ColorSpace@1a40145 transparency = 1 has alpha = false isAlphaPre = false ByteInterleavedRaster: width = 321 height = 241 #numDataElements 3 dataOff[0] = 2\"]"}
;; <=

